mapper.insert:
		MERGE INTO MC_BOARD MC1
		USING (SELECT #{MC_BOARD_SEQ} AS MC_BOARD_SEQ FROM DUAL) MC2
		ON (MC1.MC_BOARD_SEQ= MC2.MC_BOARD_SEQ)
		WHEN MATCHED THEN
		UPDATE SET
		TITLE = #{TITLE} 
		, CONTENT = #{CONTENT} 
		, MODIFIER_SEQ = #{MEMBER_SEQ} 
		, MODIFY_DATE = sysdate 
		, ITEM = #{ITEM} 
		WHEN NOT MATCHED THEN
		INSERT (
		MC_BOARD_SEQ 
		, TITLE 	
		, CONTENT 	
		, REGISTRY_DATE	
		, ITEM 	
        , CATEGORY	
        , AUTHORITY_ID	
        , MEMBER_SEQ	
		)
		values(
		  #{MC_BOARD_SEQ} 
		, #{TITLE} 
		, #{CONTENT} 
		, sysdate 
		, #{ITEM}	
        , #{CATEGORY}	
        , #{AUTHORITY_ID}	
        , #{MEMBER_SEQ}	
		)
