mapper.insert:

	SELECT MEMBER_SEQ
	FROM CIP_MEMBER
	WHERE MEMBER_SEQ = #{MEMBER_SEQ}



MERGE INTO MC_MEMBER MM1
     USING (SELECT #{MEMBER_SEQ} AS MEMBER_SEQ FROM DUAL) MM2
        ON (MM1.MEMBER_SEQ = MM2.MEMBER_SEQ)
 WHEN MATCHED THEN
    UPDATE SET
		 EMAIL = #{EMAIL} 
		, PASSWORD = #{PASSWORD} 
		, NAME = #{NAME} 
		, ADDRESS = #{ADDRESS} 
		, HOBBY = #{HOBBY} 
		, MODIFIER_SEQ = #{MODIFIER_SEQ} 
		, MODIFY_DATE = sysdate 
 WHEN NOT MATCHED THEN
    INSERT (
		MEMBER_SEQ 
		, EMAIL 
		, PASSWORD 
		, NAME 
		, ADDRESS 
		, INTEREST 
		, REGISTER_SEQ 
		, REGISTRY_DATE 
		, MODIFIER_SEQ 
		, MODIFY_DATE 
	)
	values(
		#{MEMBER_SEQ} 
		, #{EMAIL} 
		, #{PASSWORD} 
		, #{NAME} 
		, #{ADDRESS} 
		, #{HOBBY} 
		, #{REGISTER_SEQ} 
		, sysdate 
		, #{MODIFIER_SEQ} 
		, sysdate 
	)
